const socket = {
    state:{
        procedureState:0, //驱动状态 0正常 1未安装 2启动失败 3更新 4已安装
        socketState:0, //socket状态
        deviceList:[], //设备列表
        upProgess:0 ,//上传进度
        upStep:1 , //上传步骤 1启动驱动 2启动中 3连接设备 4同步数据
    },
    mutations:{
        SET_PROCEDURE_STATE:(state,procedureState) => {
            state.procedureState = procedureState
        },
        SET_SOCKET_STATE:(state,socketState) => {
            state.socketState = socketState
        },
        SET_DEVICE_LIST:(state,deviceList) => {
            state.deviceList = deviceList
        },
        SET_UP_PROGESS:(state,upProgess) => {
            state.upProgess = upProgess
        },
        SET_UP_STEP:(state,upStep) => {
            state.upStep = upStep
        }
    },
    actions:{
        setProcedureState({commit},data){
            commit('SET_PROCEDURE_STATE',data)
        },
        setSocketState({commit},data){
            commit('SET_SOCKET_STATE',data)
        },
        setDeviceList({commit},data){
            commit('SET_DEVICE_LIST',data)
        },
        setUpProgess({commit},data){
            commit('SET_UP_PROGESS',data)
        },
        setUpStep({commit},data){
            commit('SET_UP_STEP',data)
        }
    }
}

export default socket
