export default {
  name: 'TIR',
  data() {
    return {
      levels: [{
        key: '2 级高血糖',
        value: 80,
        color: '#E98C41',
        desc: '>13.9mmol/L'
      }, {
        key: '1 级高血糖',
        value: 5,
        color: '#F6C059',
        desc: '5.1-13.9mmol/L'
      }, {
        key: '目标范围内',
        value: 5,
        color: '#32BAC0',
        desc: '3.9~10.0mmol/L'
      }, {
        key: '1 级低血糖',
        value: 5,
        color: '#F43F31',
        desc: '3.0~3.8mmol/L'
      }, {
        key: '2 级低血糖',
        value: 5,
        color: '#96251C',
        desc: '<3.0mmol/L'
      }]
    };
  },
  mounted() {
    this.drawTir();
  },
  methods: {
    drawTir() {
      const canvas = document.getElementById("myCanvas");
      const ctx = canvas.getContext("2d");
      const W = this.$refs.tir.offsetWidth * 2;
      const H = this.$refs.tir.offsetHeight * 2;
      canvas.width = W;
      canvas.height = H;
      ctx.scale(2, 2);
      const barW = 40; //柱状图宽度
      const splitInterval = 1; //间隔距离
      const lineStart = barW + 5; //标识线x轴起点
      const lineWidth = 15; //标识线x轴起点
      let start_y = 10; //柱状图y轴起始点 
      let labelLineIndex = 0; //标识线是否要弯折
      for (var i = 0; i < this.levels.length; i++) {
        let value = Number(this.levels[i].value) * 2;

        // 画柱
        ctx.fillStyle = this.levels[i].color;
        ctx.fillRect(0, start_y, barW, value);
        start_y = start_y + value + splitInterval;
        if (value < 21) {
          labelLineIndex++;
        } else if (value > 30) {
          labelLineIndex = 0;
        }
        // console.log(labelLineIndex)
        let lineOneX = value > 20 ? lineStart + lineWidth : lineStart + (lineWidth - labelLineIndex * 3);
        let lineOneY = start_y - value / 2;
        let lineTwoX = lineOneX;
        // let  lineTwoY = labelLineIndex>1||(i==3&&this.levels[2].value<=20)?start_y-(value/2)+labelLineIndex*15:lineOneY
        let lineTwoY = lineOneY;
        // let  lineTwoY = labelLineIndex>1?start_y-(value/2)+labelLineIndex*20:lineOneY

        if (labelLineIndex > 1 || i == 3 && this.levels[2].value <= 20) {
          lineTwoY = start_y - value / 2 + labelLineIndex * 15;
        } else {
          lineTwoY = lineOneY;
        }
        if (i > 2 && this.levels[2].value < 30 || i == 3 && this.levels[2].value <= 20) {
          lineTwoY = start_y - value / 2 + (labelLineIndex + 1) * 15;
        } else {
          lineTwoY = lineOneY;
        }
        console.log(lineTwoY);
        let lineThreeX = lineWidth + lineStart;
        let lineThreeY = lineTwoY;

        // 标识线
        ctx.lineWidth = "1";
        ctx.strokeStyle = "#e5e5e5";
        ctx.moveTo(lineStart, start_y - value / 2);
        ctx.lineTo(lineOneX, lineOneY);
        ctx.lineTo(lineTwoX, lineTwoY);
        ctx.lineTo(lineThreeX, lineThreeY);
        ctx.stroke();

        // lengeng
        ctx.fillRect(lineThreeX + 5, lineThreeY - 4, 8, 8);

        // key
        ctx.font = "16px Arial";
        ctx.fillStyle = '#666';
        ctx.fillText(this.levels[i].key, lineThreeX + 20, lineThreeY + 5);

        // 目标范围
        ctx.fillStyle = '#666';
        i == 2 ? ctx.fillText('(目标>70%)', lineThreeX + 30, lineThreeY + 25) : 0;

        // value值
        const textWidth = ctx.measureText(this.levels[i].key).width;
        ctx.font = "16px Arial";
        ctx.fillStyle = '#000';
        ctx.fillText(this.levels[i].value, lineThreeX + 40 + textWidth, lineThreeY + 5);

        // 百分号
        const valueWidth = ctx.measureText(this.levels[i].value).width;
        ctx.fillStyle = '#666';
        ctx.fillText('%', lineThreeX + 42 + textWidth + valueWidth, lineThreeY + 5);

        // 范围
        const descWidth = ctx.measureText(this.levels[i].desc).width;
        ctx.fillText(this.levels[i].desc, W / 2 - descWidth - 10, lineThreeY + 5);
      }
    }
  }
};
