import request from '@/api/index'

// 登录方法
export function login(data) {
    return request({
        url: '/user/login/account',
        method: 'POST',
        data: data
    })
}
// 获取配置项
export function typeList(data) {
    return request({
        url: '/profile/config/get',
        method: 'POST',
        data: data
    })
}
// 添加配置项
export function create(data) {
    return request({
        url: '/profile/configdetail/create',
        method: 'POST',
        data: data
    })
}
