import request from '@/api/index'

// 登录方法
export function login(data) {
    return request({
        url: '/user/login/account',
        method: 'POST',
        data: data
    })
}
// 修改密码
export function changepassword(data) {
    return request({
        url: '/user/changepassword',
        method: 'POST',
        data: data
    })
}
// 获取配置项列表
export function configList(data) {
    return request({
        url: '/profile/config/get',
        method: 'POST',
        data: data
    })
}
// 根据配置key获取配置项
export function typeList(data) {
    return request({
        url: '/profile/configdetail/get',
        method: 'POST',
        data: data
    })
}
// 根据配置key添加配置项
export function create(data) {
    return request({
        url: '/profile/configdetail/create',
        method: 'POST',
        data: data
    })
}
// 根据配置key修改配置项
export function updata(data) {
    return request({
        url: '/profile/configdetail/update',
        method: 'POST',
        data: data
    })
}
// 网页配置
export function getWeb(data) {
    return request({
        url: '/protocol/list',
        method: 'POST',
        data: data
    })
}
// 修改网页配置
export function setWeb(data) {
    return request({
        url: '/protocol/create',
        method: 'POST',
        data: data
    })
}
