import Vue from 'vue'
import VueRouter from 'vue-router'
import {getToken} from '@/utils/auth'

Vue.use(VueRouter)

//解决编程式路由往同一地址跳转时会报错的情况
const originalPush = VueRouter.prototype.push;
const originalReplace = VueRouter.prototype.replace;
//push
VueRouter.prototype.push = function push(location, onResolve, onReject) {
  if (onResolve || onReject)
    return originalPush.call(this, location, onResolve, onReject);
  return originalPush.call(this, location).catch(err => err);
};
//replace
VueRouter.prototype.replace = function push(location, onResolve, onReject) {
  if (onResolve || onReject)
    return originalReplace.call(this, location, onResolve, onReject);
  return originalReplace.call(this, location).catch(err => err);

}

const routes = [
  {
    path: '/login',
    name: 'login',
    meta: { title: "登录"},
    component:(resolve) => require(["@/views/login/login"], resolve)
  },{
    path:'/',
    redirect: "/report/AGP",
    component:(resolve) => require(["@/views/layout/layout"], resolve),
    children:[{
      path:'/report',
      name:'report',
      redirect: "/report/AGP",
      component:(resolve) => require(["@/views/report/index"], resolve),
      icon:require('@/assets/image/icon-report.png'),
      meta:{title:'report',icon:'icon-report',isShow:true},
      children:[{
        path:'/report/AGP',
        name:'AGP',
        component:(resolve) => require(["@/views/report/AGP"], resolve),
        meta:{title:'AGP',icon:'el-icon-setting',isShow:true}
      }]
    },{
      path:'/upLoad',
      name:'upLoad',
      icon:require('@/assets/image/icon-report.png'),
      component:(resolve) => require(["@/views/Procedure/upLoad"], resolve),
      meta:{title:'upLoad',icon:'icon-upload',isShow:true}
    },{
      path:'/setting',
      name:'setting',
      icon:require('@/assets/image/icon-report.png'),
      component:(resolve) => require(["@/views/setting/old_userInfo"], resolve),
      meta:{title:'setting',icon:'icon-setting',isShow:true}
    },{
      path:'/help',
      name:'help',
      icon:require('@/assets/image/icon-report.png'),
      component:(resolve) => require(["@/views/help/help"], resolve),
      meta:{title:'help',icon:'icon-help',isShow:true}
    },
  ]
  }
]

const router = new VueRouter({
  mode: "", // 去掉url中的#
  base:'',
  routes
})
