import "core-js/modules/es.array.push.js";
import Cookies from "js-cookie";
import { account, phoneLogin, sendsmscode, protocol, verifysmscode, setPassword, phoneRegister, resetPassword } from '@/api/userApi';
import { getToken, removeToken, setToken } from '@/utils/auth';
export default {
  data() {
    var validateCheckPass = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请再次输入密码'));
      } else if (value !== this.passData.password) {
        callback(new Error('两次输入密码不一致!'));
      } else {
        callback();
      }
    };
    return {
      accountForm: {
        username: '',
        password: '',
        agree: []
      },
      phoneForm: {
        username: '',
        code: '',
        agree: []
      },
      passData: {
        password: '',
        new_password: ''
      },
      timer: '',
      forgetPassword: false,
      setPasswordTab: true,
      PasswordLoading: false,
      resetSuccess: false,
      passRules: {
        password: [{
          required: true,
          trigger: "blur",
          message: "请输入密码"
        }, {
          pattern: /^[0-9]{6,20}$/,
          message: '密码位数必须在6~20位之内',
          trigger: ['blur', 'change']
        }],
        new_password: [{
          validator: validateCheckPass,
          required: true,
          trigger: 'blur'
        }]
      },
      loading: false,
      activeName: 'login',
      loginType: 2,
      //1验证码登录，2密码登录
      codeType: 1,
      //1获取验证码 2倒计时 3重新发送
      codeTime: 60,
      smsCodeType: 0,
      //0登录、注册 2找回密码
      passwordType: 2,
      //1设置密码 2找回密码
      user_agreement_url: '',
      privacy_policy_url: '',
      accountRules: {
        username: [{
          required: true,
          message: '请输入手机号',
          trigger: 'blur'
        }, {
          pattern: /^[12][3456789]\d{9}$/,
          message: '请输入有效的11位数字手机号码',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          trigger: "blur",
          message: "请输入密码"
        }, {
          pattern: /^[0-9]{6,20}$/,
          message: '请输入6-20位密码',
          trigger: 'blur'
        }],
        agree: [{
          required: true,
          trigger: "blur",
          message: "请先阅读并同意勾选协议"
        }]
      },
      phoneRules: {
        username: [{
          required: true,
          message: '请输入手机号',
          trigger: 'blur'
        }, {
          pattern: /^[12][3456789]\d{9}$/,
          message: '请输入有效的11位数字手机号码',
          trigger: 'blur'
        }],
        code: [{
          required: true,
          trigger: 'blur',
          message: "请输入验证码"
        }],
        agree: [{
          required: true,
          trigger: "blur",
          message: "请先阅读并同意勾选协议"
        }]
      }
    };
  },
  computed: {
    comDiabled() {
      let upattern = /^[12][3456789]\d{9}$/;
      let ppattern = /^[0-9]{6,20}$/;
      if (upattern.test(this.accountForm.username) && ppattern.test(this.accountForm.password)) {
        return false;
      } else {
        return true;
      }
    },
    comDiableds() {
      let pattern = /^[12][3456789]\d{9}$/;
      if (pattern.test(this.phoneForm.username) && this.phoneForm.code) {
        return false;
      } else {
        return true;
      }
    },
    passwordDisabled() {
      if (this.passData.password && this.passData.new_password) {
        return false;
      } else {
        return true;
      }
    }
  },
  created() {},
  mounted() {
    this.getprotocol();
  },
  methods: {
    getCookie() {
      const username = Cookies.get("username");
      const password = Cookies.get("password");
      const rememberMe = Cookies.get("rememberMe");
      this.loginForm = {
        username: username === undefined ? this.loginForm.username : username,
        password: password === undefined ? this.loginForm.password : password
      };
    },
    // 登录注册切换
    tabClick(tab, e) {
      this.$refs['phoneForm'].resetFields();
      if (this.timer) {
        clearTimeout(this.timer);
      }
      this.codeTime = 60;
      this.codeType = 1;
      if (tab.name == 'register') {
        this.loginType = 2;
      }
    },
    // 切换登录方式
    changeLogin(loginType) {
      this.loginType = loginType;
      this.activeName = 'login';
      let self = this;
      this.$refs['phoneForm'].resetFields();
      this.$refs['accountForm'].resetFields();
      this.$nextTick(() => {
        self.$refs['phoneForm'].clearValidate();
        self.$refs['accountForm'].clearValidate();
      });
    },
    // 注册
    registerSubmit() {
      this.$refs.phoneForm.validate(valid => {
        if (valid) {
          this.loading = true;
          phoneRegister({
            phone_number: this.phoneForm.username,
            code: this.phoneForm.code
          }).then(response => {
            this.loading = false;
            if (response.code == 1001) {
              this.$message({
                type: 'success',
                message: '注册成功,请设置密码'
              });
              setToken(response.data.token);
              Cookies.set("username", this.phoneForm.username, {
                expires: 30
              });
              this.passwordType = 1;
              this.setPasswordTab = true;
            } else if (response.code == 1002) {
              setToken(response.data.token);
              this.setPasswordTab = true;
            } else {
              var msg = '';
              switch (response.code) {
                case 2101:
                  msg = '手机号码不合法';
                  break;
                case 2102:
                  msg = '验证码发送过于频繁';
                  break;
                case 2103:
                  msg = '无效的验证码';
                  break;
                case 2104:
                  msg = '验证码已失效，请重新获取';
                  break;
                case 2201:
                  msg = '该账号已注册，请登录';
                  break;
              }
              this.$message({
                type: 'error',
                message: msg
              });
            }
          }).catch(res => {
            console.log(res);
          });
        }
      });
    },
    // 账号密码
    accountLogin() {
      this.$refs.accountForm.validate(valid => {
        if (valid) {
          this.loading = true;
          account({
            username: this.accountForm.username,
            password: this.$md5(this.accountForm.password)
          }).then(response => {
            this.loading = false;
            if (response.code == 1000) {
              this.$message({
                type: 'success',
                message: '登录成功'
              });
              setToken(response.data.token);
              Cookies.set("username", this.accountForm.username, {
                expires: 30
              });
              this.$router.push('/');
            } else if (response.code == 1002) {
              setToken(response.data.token);
              this.passwordType = 1;
              this.setPasswordTab = true;
            } else {
              var msg = '';
              switch (response.code) {
                case 2202:
                  msg = '用户不存在';
                  break;
                case 2203:
                  msg = '请先设置密码';
                  break;
                case 2204:
                  msg = '密码不正确';
                  break;
              }
              this.$message({
                type: 'error',
                message: msg
              });
            }
          }).catch(res => {
            console.log(res);
          });
        }
      });
    },
    // 手机号登录
    phoneSubmit() {
      this.$refs.phoneForm.validate(valid => {
        if (valid) {
          this.loading = true;
          phoneLogin({
            phone_number: this.phoneForm.username,
            code: this.phoneForm.code
          }).then(response => {
            this.loading = false;
            if (response.code == 1000) {
              this.$message({
                type: 'success',
                message: '登录成功'
              });
              setToken(response.data.token);
              Cookies.set("username", this.phoneForm.username, {
                expires: 30
              });
              this.$router.push('/');
            } else if (response.code == 1002) {
              setToken(response.data.token);
              this.passwordType = 1;
              this.setPasswordTab = true;
            } else {
              var msg = '';
              switch (response.code) {
                case 2101:
                  msg = '手机号码不合法';
                  break;
                case 2102:
                  msg = '验证码发送过于频繁';
                  break;
                case 2103:
                  msg = '无效的验证码';
                  break;
                case 2104:
                  msg = '验证码已失效，请重新获取';
                  break;
              }
              this.$message({
                type: 'error',
                message: msg
              });
            }
          }).catch(res => {
            console.log(res);
          });
        }
      });
    },
    // 获取验证码
    getCode(smsType) {
      this.$refs['phoneForm'].validateField('username', value => {
        if (!value) {
          this.codeType = 2;
          this.smsCodeType = smsType;
          this.timeOut();
          this.setCode();
        }
      });
    },
    // 验证码倒计时
    timeOut() {
      let time = this.codeTime;
      let that = this;
      let timer = setTimeout(function () {
        if (time > 1) {
          time--;
          that.codeTime = time;
          that.timeOut();
        } else {
          that.codeType = 3;
          that.codeTime = 60;
        }
      }, 1000);
      this.timer = timer;
    },
    // 发送验证码
    setCode() {
      let type = this.smsCodeType;
      sendsmscode({
        phone_number: this.phoneForm.username,
        type: type
      }).then(response => {
        this.loading = false;
        if (response.code == 1000) {
          this.$message({
            type: 'success',
            message: '发送成功'
          });
        } else {
          var msg = '';
          switch (response.code) {
            case 2102:
              msg = '验证码发送过于频繁';
              break;
            case 2103:
              msg = '验证码不正确';
              break;
            case 2104:
              msg = '验证码已过期';
              break;
          }
          this.$message({
            type: 'error',
            message: msg
          });
        }
      }).catch(res => {
        console.log(res);
      });
    },
    // 验证验证码
    verifyCode() {
      this.$refs.phoneForm.validate(valid => {
        if (valid) {
          this.loading = true;
          verifysmscode({
            phone_number: this.phoneForm.username,
            code: this.phoneForm.code,
            type: 2
          }).then(response => {
            this.loading = false;
            if (response.code == 1000) {
              this.setPasswordTab = true;
              this.forgetPassword = false;
              this.passwordType = 2;
            } else {
              var msg = '';
              switch (response.code) {
                case 2101:
                  msg = '手机号码不合法';
                  break;
                case 2102:
                  msg = '验证码发送过于频繁';
                  break;
                case 2103:
                  msg = '无效的验证码';
                  break;
                case 2202:
                  msg = '用户不存在';
                  break;
              }
              this.$message({
                type: 'error',
                message: msg
              });
            }
          }).catch(res => {
            console.log(res);
          });
        }
      });
    },
    // 忘记密码
    showForget() {
      this.forgetPassword = true;
      if (this.timer) {
        clearTimeout(this.timer);
      }
      this.codeTime = 60;
      this.codeType = 1;
      this.phoneForm.username = '';
      this.phoneForm.code = '';
    },
    // 返回登录框
    handelBack() {
      this.forgetPassword = false;
      if (this.timer) {
        clearTimeout(this.timer);
      }
      this.codeTime = 60;
      this.codeType = 1;
      this.phoneForm.username = '';
      this.phoneForm.code = '';
    },
    // 获取隐私和条款
    getprotocol() {
      protocol({}).then(response => {
        if (response.code == 1000) {
          this.user_agreement_url = response.data.user_agreement_url;
          this.privacy_policy_url = response.data.privacy_policy_url;
        } else {
          this.$message({
            type: 'error',
            message: response.msg
          });
        }
      }).catch(res => {
        console.log(res);
      });
    },
    // 设置密码
    changePassword() {
      this.$refs.passData.validate(valid => {
        if (valid) {
          setPassword({
            password: this.$md5(this.passData.password),
            new_password: this.$md5(this.passData.new_password)
          }).then(response => {
            this.PasswordLoading = false;
            if (response.code == 1000) {
              this.$message({
                type: 'success',
                message: '设置成功'
              });
              this.$router.push('/userInfo');
            } else {
              this.$message({
                type: 'error',
                message: response.msg
              });
            }
          }).catch(res => {
            console.log(res);
          });
        }
      });
    },
    // 重置密码
    resetPassword() {
      this.$refs.passData.validate(valid => {
        if (valid) {
          resetPassword({
            phone_number: this.phoneForm.username,
            code: this.phoneForm.code,
            new_password: this.$md5(this.passData.new_password)
          }).then(response => {
            this.PasswordLoading = false;
            if (response.code == 1000) {
              this.resetSuccess = true;
              let self = this;
              setTimeout(function () {
                self.setPasswordTab = false;
                self.forgetPassword = false;
              }, 2000);
            } else {
              this.$message({
                type: 'error',
                message: response.msg
              });
            }
          }).catch(res => {
            console.log(res);
          });
        }
      });
    }
  }
};
