import "core-js/modules/es.array.push.js";
import TChart from '@/views/components/TChart';
import { hasEventListener } from "@/utils/utils";
import { formatDate, formatTime } from '@/utils/formatTime';
import { TIRUtils } from "@/utils/algorithm/TIR";
import { GlucoseUtils } from "@/utils/algorithm/Glucose";
import { Divider } from 'element-ui';
export default {
  data() {
    return {
      initOptions: {
        renderer: 'svg'
      },
      option: {
        tooltip: {
          show: true,
          trigger: 'axis',
          confine: true
        },
        title: [],
        grid: [],
        xAxis: [],
        yAxis: [],
        visualMap: [],
        series: []
      },
      dayList: [],
      week: [],
      gridW: 0
    };
  },
  components: {
    TChart
  },
  created() {},
  mounted() {
    console.log(formatTime(new Date()), '组件渲染完成');
    // 监听云数据 
    this.$bus.$on('getDatas', data => {
      this.handelIng(data);
    });
    // 监听vuex数据
    this.$bus.$on('getVuexTag', data => {
      let result = this.$store.state.report.day[data];
      this.dayList = result;
      this.$nextTick(() => {
        this.getOption(result);
      });
    });
  },
  methods: {
    /**
    * 循环对比选中的开始时间和结束时间，有数据插入，没有数据为空
    * currentDate 记录开始时间，每次循环完成加一天
    * dayList 分割后最终的数据数组
    * dayList 参数集 day:日期 week:周 value:按时刻分割的数据 tir:每日的tir max:所有数据的最大值，大于400则等于400
    * value = dayArray 画图需要的按时刻分割的1440的空数组(一分钟一个数据点，一天1440个数据点对应1440个时刻)，需要把大于400低于40的值置为40和400
    * dayArrays 计算tir所需要的数组，不需要重新赋值
    */
    handelIng(data) {
      let s_date = data[0].split('/')[0];
      let e_date = data[0].split('/')[1];
      let arrays = _.uniqBy(data[1], 'DataTs'); //去重
      let array = _.filter(arrays, function (val) {
        //过滤掉无效数据
        return val.Value >= 20 && val.Value <= 800;
      });
      let currentDate = new Date(s_date);
      let dayList = [];
      while (currentDate <= new Date(e_date)) {
        let dayArray = new Array(24 * 60);
        let dayArrays = new Array(24 * 60);
        array.forEach(item => {
          let moment = Number(formatDate(item.DataTs * 1000, 'HH')) * 60 + Number(formatDate(item.DataTs * 1000, 'MM'));
          let value = item.Value;
          if (item.Value > 400 && item.Value <= 800) value = 400;
          if (item.Value >= 20 && item.Value < 40) value = 40;
          if (formatDate(item.DataTs * 1000, 'mm.dd') == formatDate(currentDate, 'mm.dd')) {
            dayArray[moment] = value ? GlucoseUtils.mgdlToMmol(value) : null;
            dayArrays[moment] = item.Value;
          }
        });
        dayList.push({
          day: formatDate(currentDate, 'mm.dd'),
          week: formatDate(currentDate, 'WW'),
          value: dayArray,
          max: GlucoseUtils.mgdlToMmol(_.maxBy(array, 'Value').Value > 400 ? 400 : _.maxBy(array, 'Value').Value),
          tir: TIRUtils.getTIRResult(dayArrays) ? (Number(TIRUtils.getTIRResult(dayArrays).normalRate) * 100).toFixed(1) : ''
        });
        currentDate.setDate(new Date(currentDate).getDate() + 1);
      }
      this.dayList = dayList;
      this.$store.dispatch('setDay', {
        key: data[0],
        value: dayList
      }); //处理完数据存vuex
      this.$nextTick(() => {
        this.getOption(this.dayList);
      });
    },
    // 渲染数据
    getOption(data) {
      let dayList = data;
      let gridW = Math.floor((this.$refs.day.clientWidth - 240) / 7);
      this.gridW = gridW;
      let xData = Array.from({
        length: 60 * 24
      }, (item, index) => index);
      let max = data[0].max < 13.9 ? 13.9 : data[0].max;
      this.option = {
        tooltip: {
          show: true,
          trigger: 'axis',
          confine: true,
          axisPointer: {
            type: 'none',
            axis: 'x'
          },
          formatter(params) {
            let h = Math.floor(Number(params[0].axisValue) / 60) < 10 ? '0' + Math.floor(Number(params[0].axisValue) / 60) : Math.floor(Number(params[0].axisValue) / 60);
            let m = Number(params[0].axisValue) % 60 < 10 ? '0' + Number(params[0].axisValue) % 60 : Number(params[0].axisValue) % 60;
            let moment = h + ':' + m;
            let html = "<div class='tooltip-box' >" + " <div class='tooltips-val'>" + " <span class='tooltips-val-num'>" + params[0].value + "</span>mmol/L" + "</div>" + " <div class='tooltips-date'>" + dayList[params[0].axisIndex].day + ' ' + moment + " </div>" + " </div>";
            return html;
          },
          extraCssText: 'box-shadow: 0 2px 10px rgba(195,245,247,1);'
        },
        title: [],
        grid: [],
        xAxis: [],
        yAxis: [],
        visualMap: [],
        series: []
      };
      dayList.forEach((item, index) => {
        if (index > 14) {
          return;
        }
        let tirClass = item.tir > 70 ? 'tir' : 'tirs';
        this.option.title.push({
          text: item.tir ? ['{date|' + item.day + '}{tirLable|TIR:}{' + tirClass + '|' + item.tir + '%}'] : ['{date|' + item.day + '}{tir|TIR:--}'],
          left: index == 0 ? 90 : index < 7 && index > 0 ? index * (gridW + 15) + 90 : index % 7 * (gridW + 15) + 90,
          top: index >= 7 ? 172 * Math.floor(index / 7) : 2,
          right: 10,
          textStyle: {
            rich: {
              date: {
                fontSize: 16,
                color: '#333',
                width: (gridW - 10) / 2,
                align: 'left'
              },
              tirLable: {
                fontSize: 20,
                width: 25,
                color: '#333',
                align: 'right'
              },
              tir: {
                fontSize: 20,
                width: 60,
                color: '#333',
                align: 'left'
              },
              tirs: {
                fontSize: 20,
                width: 60,
                color: '#F43F31',
                align: 'left'
              }
            }
          }
        });
        this.option.grid.push({
          show: false,
          left: index == 0 ? 90 : index < 7 && index > 0 ? index * (gridW + 15) + 90 : index % 7 * (gridW + 15) + 90,
          right: 0,
          top: index >= 7 ? 170 * Math.floor(index / 7) : 0,
          width: gridW,
          height: 120,
          borderColor: '#666',
          borderWidth: 0.5
        });
        this.option.xAxis.push({
          type: 'category',
          gridIndex: index,
          show: true,
          axisLine: {
            show: false
          },
          data: xData,
          axisLabel: {
            formatter: function (value, indexs) {
              if (indexs == 0) {
                return '00:00';
              } else if (index == dayList.length - 1 && indexs == item.value.length - 1 || index % 7 == 6 && indexs == item.value.length - 1) {
                return '00:00';
              } else if (indexs == item.value.length / 2 - 1) {
                return '12:00';
              }
            },
            interval: 0,
            color: 'var(--color-black-60)',
            fontSize: 16,
            margin: 15
          },
          axisTick: {
            show: false
          }
        });
        this.option.yAxis.push({
          type: 'value',
          show: false,
          gridIndex: index,
          min: 0,
          max: Math.ceil(max / 3) * 3 + 8
        });
        this.option.visualMap.push({
          type: "piecewise",
          show: false,
          pieces: [{
            gt: 0,
            lt: 3.9,
            color: "var(--color-error)" //大于0小于12为红色
          }, {
            gt: 13.9,
            color: "var(--color-warning" //大于12区间为红色
          }, {
            gt: 3.9,
            lt: 13.9,
            color: "var(--color-primary" //大于12区间为红色
          }]
        });
        this.option.series.push({
          type: 'line',
          data: item.value,
          xAxisIndex: index,
          yAxisIndex: index,
          showSymbol: _.compact(item.value).length > 1 ? false : true,
          symbolSize: 1,
          symbol: 'circle',
          emphasis: {
            disabled: true
          },
          lineStyle: {
            width: 1
          },
          markLine: {
            symbol: 'none',
            // animation:false,
            data: [
            // {
            //     xAxis:xData.length/2-1,
            //     lineStyle: {
            //         color: 'var(--color-black-10)',
            //         width: 1,
            //         type:'dashed'
            //     },
            //     label:{
            //         show:false
            //     }
            // } ,
            {
              yAxis: 3.9,
              lineStyle: {
                width: 0
              },
              label: {
                show: index % 7 == 0 ? true : false,
                position: 'start',
                distance: 14,
                fontSize: 16,
                color: '#666'
              }
            }, {
              yAxis: 13.9,
              lineStyle: {
                width: 0
              },
              label: {
                show: index % 7 == 0 ? true : false,
                position: 'start',
                fontSize: 16,
                distance: 14,
                color: '#666'
              }
            }]
          },
          markArea: {
            data: [[{
              yAxis: 3.9
            }, {
              yAxis: 13.9
            }]],
            itemStyle: {
              color: 'rgba(50, 186, 192, 0.08)'
            }
          }
        });
      });
    },
    // 管理数据
    handelIngs(data) {
      console.log(data[0]);
      let array = data[1];
      // 将数组按天分割
      let dayMap = new Map();
      array.forEach(item => {
        item.day = formatDate(item.DataTs * 1000, 'mm.dd');
        item.week = formatDate(item.DataTs * 1000, 'WW');
        item.moment = Number(formatDate(item.DataTs * 1000, 'HH')) * 60 + Number(formatDate(item.DataTs * 1000, 'MM'));
        item.dayArray = new Array(24 * 60);
        item.dayArray[item.moment] == GlucoseUtils.mgdlToMmol(item.Value);
        item.dayArrayCopy = new Array(24 * 60);
        item.dayArrayCopy[item.moment] = item.Value;
        dayMap.has(item.day) ? dayMap.get(item.day).value[item.moment] = GlucoseUtils.mgdlToMmol(item.Value) : dayMap.set(item.day, {
          value: item.dayArray,
          week: item.week,
          tir: TIRUtils.getTIRResult(item.dayArrayCopy) ? TIRUtils.getTIRResult(item.dayArrayCopy).normalRate : ''
        });
      });
      // x轴数据
      this.dayList = [...dayMap];
      this.$store.dispatch('setDay', {
        key: data[0],
        value: [...dayMap]
      }); //处理完数据存vuex
      this.$nextTick(() => {
        this.getOptions(this.dayList);
      });
    },
    // 渲染数据
    getOptions(data) {
      let dayList = data;
      let gridW = Math.floor((this.$refs.day.clientWidth - 240) / 7);
      this.gridW = gridW;
      let xData = Array.from({
        length: 60 * 24
      });
      this.option = {
        title: [],
        grid: [],
        xAxis: [],
        yAxis: [],
        visualMap: [],
        series: []
      };
      dayList.forEach((item, index) => {
        if (index > 13) {
          return;
        }
        this.option.title.push({
          text: ['{date|' + dayList[index][0] + '}{tir|TIR:' + dayList[index][1].tir + '%}'],
          left: index == 0 ? 90 : index < 7 && index > 0 ? index * (gridW + 15) + 90 : (index - 7) * (gridW + 15) + 90,
          top: index >= 7 ? 172 : 2,
          textStyle: {
            rich: {
              date: {
                fontSize: 16,
                color: '#666',
                width: (gridW - 10) / 2,
                align: 'left'
              },
              tir: {
                fontSize: 16,
                color: '#666',
                width: (gridW - 10) / 2,
                align: 'right'
              }
            }
          }
        });
        this.option.grid.push({
          show: true,
          borderRadius: 14,
          left: index == 0 ? 90 : index < 7 && index > 0 ? index * (gridW + 15) + 90 : (index - 7) * (gridW + 15) + 90,
          right: 0,
          top: index >= 7 ? 170 : 0,
          width: gridW,
          height: 120,
          borderColor: '#e5e5e5',
          borderWidth: 0.5
        });
        this.option.xAxis.push({
          type: 'category',
          gridIndex: index,
          show: true,
          axisLine: {
            show: false
          },
          data: xData,
          axisLabel: {
            formatter: function (value, indexs) {
              if (indexs == 0) {
                return '00:00';
              } else if (indexs == dayList[index][1].value.length - 1 && index == 6 || indexs == dayList[index][1].value.length - 1 && index == 13) {
                return '00:00';
              } else if (indexs == dayList[index][1].value.length / 2 - 1) {
                return '12:00';
              }
            },
            interval: 0,
            color: 'var(--color-black-40)',
            fontSize: 16,
            margin: 15
          },
          axisTick: {
            show: false
          }
        });
        this.option.yAxis.push({
          type: 'value',
          show: false,
          gridIndex: index,
          min: 0,
          max: 16
        });
        this.option.visualMap.push({
          type: "piecewise",
          show: false,
          pieces: [{
            gt: 0,
            lt: 3.9,
            color: "var(--color-error)" //大于0小于12为红色
          }, {
            gt: 10,
            color: "var(--color-warning" //大于12区间为红色
          }, {
            gt: 3.9,
            lt: 10,
            color: "var(--color-primary" //大于12区间为红色
          }]
        });
        this.option.series.push({
          type: 'line',
          data: dayList[index][1].value,
          xAxisIndex: index,
          yAxisIndex: index,
          showSymbol: false,
          lineStyle: {
            width: 1
          },
          markLine: {
            symbol: 'none',
            animation: false,
            data: [{
              xAxis: xData.length / 2 - 1,
              lineStyle: {
                color: 'var(--color-black-10)',
                width: 1,
                type: 'dashed'
              },
              label: {
                show: false
              }
            }, {
              yAxis: 3.9,
              lineStyle: {
                width: 0
              },
              label: {
                show: index == 0 || index == 7 ? true : false,
                position: 'start',
                distance: 14,
                fontSize: 16,
                color: '#666'
              }
            }, {
              yAxis: 10,
              lineStyle: {
                width: 0
              },
              label: {
                show: index == 0 || index == 7 ? true : false,
                position: 'start',
                fontSize: 16,
                distance: 14,
                color: '#666'
              }
            }]
          },
          markArea: {
            data: [[{
              yAxis: 3.9
            }, {
              yAxis: 10
            }]],
            itemStyle: {
              color: 'rgba(50, 186, 192, 0.08)'
            }
          }
        });
      });
    }
  },
  beforeDestroy() {
    this.$bus.$off('getDatas');
    this.$bus.$off('getVuexTag');
  }
};
