export default {
  name: 'TIR',
  data() {
    return {
      levels: [{
        key: '2 级高血糖',
        value: 40,
        color: '#E98C41',
        desc: '>13.9mmol/L'
      }, {
        key: '1 级高血糖',
        value: 10,
        color: '#F6C059',
        desc: '10.1-13.9mmol/L'
      }, {
        key: '目标范围内',
        value: 20,
        color: '#32BAC0',
        desc: '3.9~10.0mmol/L'
      }, {
        key: '1 级低血糖',
        value: 5,
        color: '#F43F31',
        desc: '3.0~3.8mmol/L'
      }, {
        key: '2 级低血糖',
        value: 5,
        color: '#96251C',
        desc: '<3.0mmol/L'
      }]
    };
  },
  mounted() {
    this.drawTir();
  },
  methods: {
    drawTir() {
      const canvas = document.getElementById("myCanvas");
      const ctx = canvas.getContext("2d");
      const W = this.$refs.tir.offsetWidth;
      const H = this.$refs.tir.offsetHeight * 2;
      canvas.width = W;
      canvas.height = H;
      const barW = 40; //柱状图宽度
      const splitInterval = 1; //间隔距离
      const lineStart = barW + 5; //标识线x轴起点
      const lineWidth = 15; //标识线x轴起点
      let start_y = 0; //柱状图y轴起始点 
      let labelLineIndex = 0; //标识线是否要弯折
      for (var i = 0; i < this.levels.length; i++) {
        let value = Number(this.levels[i].value) * 2;
        ctx.fillStyle = this.levels[i].color;
        ctx.fillRect(0, start_y, barW, value);
        start_y = start_y + value + splitInterval;
        ctx.lineWidth = "1";
        ctx.strokeStyle = "#e5e5e5";
        ctx.moveTo(lineStart, start_y - value / 2);
        if (value < 20) {
          labelLineIndex++;
        } else {
          labelLineIndex = 0;
        }
        let lineOneX = value > 20 ? lineStart + lineWidth : lineStart + (lineWidth - labelLineIndex * 3);
        let lineOneY = start_y - value / 2;
        let lineTwoX = lineOneX;
        let lineTwoY = labelLineIndex > 1 || i == 3 && this.levels[2].value <= 20 ? start_y - value / 2 + labelLineIndex * 20 : lineOneY;
        let lineThreeX = labelLineIndex > 1 || i == 3 && this.levels[2].value <= 20 ? lineStart + lineWidth : lineTwoX;
        let lineThreeY = labelLineIndex > 1 || i == 3 && this.levels[2].value <= 20 ? start_y - value / 2 + labelLineIndex * 20 : lineTwoY;
        ctx.lineTo(lineOneX, lineOneY);
        ctx.lineTo(lineTwoX, lineTwoY);
        ctx.lineTo(lineThreeX, lineThreeY);
        ctx.stroke();
        ctx.font = "16px Arial";
        ctx.fillStyle = '#666';
        //ctx.fillText(this.levels[i].key,lineThreeX+10,labelLineIndex>1?lineThreeY+5:lineOneY+5);
        ctx.fillText(this.levels[i].key, lineThreeX + 10, lineThreeY + 5);
      }
    }
  }
};
