import request from '@/api/index'

// 登录方法
export function login(data) {
    return request({
        url: '/user/login/account',
        method: 'POST',
        data: data
    })
}
// 根据配置key获取配置项
export function typeList(data) {
    return request({
        url: '/api/profile/configdetail/get',
        method: 'POST',
        data: data
    })
}
// 添加配置项
export function create(data) {
    return request({
        url: '/profile/configdetail/create',
        method: 'POST',
        data: data
    })
}
// 网页配置
export function getWeb(data) {
    return request({
        url: '/api/protocol/last',
        method: 'POST',
        data: data
    })
}
