import Vue from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)

const report = {
    state:{
        chooseDateList:[],//选择过的日期数据集
        tir:{},
    },
    mutations:{
        SET_CHOOSE_DATE_LIST(state,chooseDate){
            if( _.indexOf(state.chooseDateList,chooseDate)!=-1) return;
            console.log(chooseDate,'store')
            state.chooseDateList.push(chooseDate)
        },
        SET_TIR(state,data){
            if(_.has(state.tir, data))return;
            Vue.set(state.tir,data.key,data.value)
        }
    },
    actions:{
        setChooseDateList({commit},data){
            commit('SET_CHOOSE_DATE_LIST',data)
        },
        setTir({commit},data){
            commit('SET_TIR',data)
        }
    }
}
