import { Message } from "element-ui";
var socket = null; //实例对象
var lockReconnect = false; //是否真正建立连接
var timeoutnum = null; //重连时间间隔
var port = [8971,8972,8973,8974,8975,8976,8977,8978,8979,8980]; //端口号
var repeat = 0; //重连次数

const initwebSocket = async()=>{
    if("WebSocket" in window){
        var wsUrl = 'ws://ivd.cgm3.com:'+port[repeat]+'/uploader'
        console.log(wsUrl,'连接地址')
        socket = new WebSocket(wsUrl);
        socket.onerror = webSocketOnError;
        socket.onmessage = webSocketOnMessage;
        socket.onclose = closeWebsocket;
        socket.onopen = openWebsocket;
    }else{
        Message.error({
            showClose: true,
            message: '您的浏览器不支持websocket，请更换Chrome或者Firefox'
        });
    }
}


//建立连接
const openWebsocket = (e) => {
    console.log(e,'已连接')
    lockReconnect = true

}
// 重新连接
const reconnect = () =>{
    if(lockReconnect){
        return ;
    }
    if(repeat>9){
        console.log('WebSocket 连接失败，端口都不被连接');
    }else{
        console.log(`正在尝试第 ${repeat} 次重连...`);
        setTimeout(function(){
            repeat++
            initwebSocket()
        },timeoutnum)
    }
}


const sendWebsocket =(e) =>{
    socket.send(`我发消息了`);
}


const webSocketOnError =(e) => {
    console.log('发生错误',e)
    reconnect();

}  

const webSocketOnMessage=(e) => {
    console.log('收到消息',e)
}

const closeWebsocket=(e) => {
    console.log('连接已关闭',e)
    lockReconnect = false
} 
//断开连接
const close =() => {
    //WebSocket对象也有发送和关闭的两个方法，只需要在自定义方法中分别调用send()和close()即可实现。
      socket.close();
}    

export default{initwebSocket}
